&nbsp;{\n&nbsp;&nbsp;$(&#39;input[name=&quot;to&quot;]&#39;).daterangepicker({&nbsp;&nbsp;&nbsp;&quot;autoApply&quot;:&nbsp;true,&nbsp;//选择日期后自动提交;只有在不显示时间的时候起作用timePicker:false\n&nbsp;&nbsp;&nbsp;singleDatePicker:&nbsp;true,&nbsp;//单日历\n&nbsp;&nbsp;&nbsp;showDropdowns:&nbsp;true,&nbsp;//年月份下拉框\n&nbsp;&nbsp;&nbsp;timePicker:&nbsp;true,&nbsp;//显示时间\n&nbsp;&nbsp;&nbsp;timePicker24Hour:&nbsp;true,&nbsp;//时间制\n&nbsp;&nbsp;&nbsp;timePickerSeconds:&nbsp;true,&nbsp;//时间显示到秒\n&nbsp;&nbsp;&nbsp;//&nbsp;startDate:&nbsp;moment().hours(0).minutes(0).seconds(0),&nbsp;//设置开始日期\n&nbsp;&nbsp;&nbsp;maxDate:&nbsp;moment(new&nbsp;Date()),&nbsp;//设置最大日期\n&nbsp;&nbsp;&nbsp;minDate:&nbsp;minDate,&nbsp;&nbsp;&nbsp;&quot;opens&quot;:&nbsp;&quot;center&quot;,\n&nbsp;&nbsp;&nbsp;showWeekNumbers:&nbsp;true,\n&nbsp;&nbsp;&nbsp;locale:&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;format:&nbsp;&quot;YYYY-MM-DD&nbsp;HH:mm:ss&quot;,&nbsp;//设置显示格式\n&nbsp;&nbsp;&nbsp;&nbsp;applyLabel:&nbsp;&#39;确定&#39;,&nbsp;//确定按钮文本\n&nbsp;&nbsp;&nbsp;&nbsp;cancelLabel:&nbsp;&#39;取消&#39;,&nbsp;//取消按钮文本\n&nbsp;&nbsp;&nbsp;&nbsp;daysOfWeek:&nbsp;[&#39;日&#39;,&nbsp;&#39;一&#39;,&nbsp;&#39;二&#39;,&nbsp;&#39;三&#39;,&nbsp;&#39;四&#39;,&nbsp;&#39;五&#39;,&nbsp;&#39;六&#39;],\n&nbsp;&nbsp;&nbsp;&nbsp;monthNames:&nbsp;[&#39;一月&#39;,&nbsp;&#39;二月&#39;,&nbsp;&#39;三月&#39;,&nbsp;&#39;四月&#39;,&nbsp;&#39;五月&#39;,&nbsp;&#39;六月&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;七月&#39;,&nbsp;&#39;八月&#39;,&nbsp;&#39;九月&#39;,&nbsp;&#39;十月&#39;,&nbsp;&#39;十一月&#39;,&nbsp;&#39;十二月&#39;\n&nbsp;&nbsp;&nbsp;&nbsp;],\n&nbsp;&nbsp;&nbsp;&nbsp;firstDay:&nbsp;1\n&nbsp;&nbsp;&nbsp;},\n&nbsp;&nbsp;},&nbsp;function(s)&nbsp;{\n&nbsp;&nbsp;&nbsp;fromDate(s)\n&nbsp;&nbsp;});\n&nbsp;}\n&nbsp;toDate();123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263</pre><pre class=\"prettyprint\" style=\"box-sizing: border-box; outline: 0px; padding: 8px 16px 4px 56px; margin-top: 0px; margin-bottom: 24px; position: relative; overflow-y: hidden; overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; word-break: break-all; background-color: rgb(246, 248, 250); border: none;\">var&nbsp;start&nbsp;=&nbsp;moment(new&nbsp;Date());&nbsp;function&nbsp;cb(start)&nbsp;{\n&nbsp;&nbsp;$(&#39;#divDateSelect&nbsp;span&#39;).html(start.format(&#39;YYYY-MM-DD&nbsp;HH:mm:ss&#39;));\n&nbsp;}\n&nbsp;$(&#39;#divDateSelect&#39;).daterangepicker({&nbsp;&nbsp;&quot;autoApply&quot;:&nbsp;true,&nbsp;//选择日期后自动提交;只有在不显示时间的时候起作用timePicker:false\n&nbsp;&nbsp;singleDatePicker:&nbsp;true,&nbsp;//单日历\n&nbsp;&nbsp;showDropdowns:&nbsp;true,&nbsp;//年月份下拉框\n&nbsp;&nbsp;//&nbsp;timePicker:&nbsp;true,&nbsp;//显示时间\n&nbsp;&nbsp;timePicker24Hour:&nbsp;true,&nbsp;//时间制\n&nbsp;&nbsp;timePickerSeconds:&nbsp;true,&nbsp;//时间显示到秒\n&nbsp;&nbsp;startDate:&nbsp;moment().hours(0).minutes(0).seconds(0),&nbsp;//设置开始日期\n&nbsp;&nbsp;maxDate:&nbsp;moment(new&nbsp;Date()),&nbsp;//设置最大日期\n&nbsp;&nbsp;&quot;opens&quot;:&nbsp;&quot;center&quot;,\n&nbsp;&nbsp;showWeekNumbers:&nbsp;true,\n&nbsp;&nbsp;locale:&nbsp;{\n&nbsp;&nbsp;&nbsp;format:&nbsp;&quot;YYYY-MM-DD&nbsp;HH:mm:ss&quot;,&nbsp;//设置显示格式\n&nbsp;&nbsp;&nbsp;applyLabel:&nbsp;&#39;确定&#39;,&nbsp;//确定按钮文本\n&nbsp;&nbsp;&nbsp;cancelLabel:&nbsp;&#39;取消&#39;,&nbsp;//取消按钮文本\n&nbsp;&nbsp;&nbsp;daysOfWeek:&nbsp;[&#39;日&#39;,&nbsp;&#39;一&#39;,&nbsp;&#39;二&#39;,&nbsp;&#39;三&#39;,&nbsp;&#39;四&#39;,&nbsp;&#39;五&#39;,&nbsp;&#39;六&#39;],\n&nbsp;&nbsp;&nbsp;monthNames:&nbsp;[&#39;一月&#39;,&nbsp;&#39;二月&#39;,&nbsp;&#39;三月&#39;,&nbsp;&#39;四月&#39;,&nbsp;&#39;五月&#39;,&nbsp;&#39;六月&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&#39;七月&#39;,&nbsp;&#39;八月&#39;,&nbsp;&#39;九月&#39;,&nbsp;&#39;十月&#39;,&nbsp;&#39;十一月&#39;,&nbsp;&#39;十二月&#39;\n&nbsp;&nbsp;&nbsp;],\n&nbsp;&nbsp;&nbsp;firstDay:&nbsp;1\n&nbsp;&nbsp;},\n&nbsp;},&nbsp;cb);\n&nbsp;cb(start);123456789101112131415161718192021222324252627</pre><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 8px 0px 16px; font-size: 22px; color: rgb(79, 79, 79); line-height: 30px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255);\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>效果图</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255);\">第一部分&nbsp;<br/><img src=\"https://img-blog.csdn.net/20180608152254301?watermark/2/text/aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L1pENzE3ODIyMDIz/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70\" alt=\"这里写图片描述\" title=\"\"/>&nbsp;<br/>第二部分&nbsp;<br/><img src=\"https://img-blog.csdn.net/20180608152329540?watermark/2/text/aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L1pENzE3ODIyMDIz/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70\" alt=\"这里写图片描述\" title=\"\"/>&nbsp;<br/>第三部分&nbsp;<br/>单个单日历实现第一部分的效果;原理为在确定好开始日期后;设置选择结束日期日历的最小选择日期;在结束日期选中后;设置开始日期的最大选择日期;&nbsp;<br/><img src=\"https://img-blog.csdn.net/20180608152344810?watermark/2/text/aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L1pENzE3ODIyMDIz/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70\" alt=\"这里写图片描述\" title=\"\"/>&nbsp;<br/>第四部分&nbsp;<br/><img src=\"https://img-blog.csdn.net/20180608152506432?watermark/2/text/aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L1pENzE3ODIyMDIz/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70\" alt=\"这里写图片描述\" title=\"\"/></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255);\">备注：&nbsp;<br/>1. moment.js使用了数组的indexOf()方法;但IE8不支持;需要引入兼容代码;代码地址<a href=\"https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Array/indexOf\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(103, 149, 181); text-decoration-line: none; cursor: pointer; word-break: break-all;\">https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Array/indexOf</a>在polyfill下;&nbsp;<br/>2. 在IE8下;双日历的范围选择出现连个日历竖直排列问题;解决方法为给存放两个日历的盒子设置固定的宽度,足以放下两个日历的div;再把两个日历的div设置float:left即可;&nbsp;<br/>3. 官网地址;选项设置:&nbsp;<a href=\"http://www.daterangepicker.com/#options\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(103, 149, 181); text-decoration-line: none; cursor: pointer; word-break: break-all;\">http://www.daterangepicker.com/#options</a>　　&nbsp;<br/>例子:&nbsp;<a href=\"http://www.daterangepicker.com/#examples\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(103, 149, 181); text-decoration-line: none; cursor: pointer; word-break: break-all;\">http://www.daterangepicker.com/#examples</a>；&nbsp;<br/>4. 希望本文能够帮助初识daterangepicker的朋友.</p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255);\">我当时具体实现的例子，希望可以帮到你们。&nbsp;<br/><a href=\"https://download.csdn.net/download/zd717822023/10467382\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(103, 149, 181); text-decoration-line: none; cursor: pointer; word-break: break-all;\">https://download.csdn.net/download/zd717822023/10467382</a></p><p><br/></p>', '2018-06-09 19:17:57', '2018-06-09 19:17:57', '1', '4', '3', '0');
INSERT INTO `forum` VALUES ('33', 'javaweb学习总结(二十五)——jsp简单标签开发(一)', '<h2 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 21px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">一、简单标签(SimpleTag)</h2><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　由于传统标签使用三个标签接口来完成不同的功能，显得过于繁琐，不利于标签技术的推广， SUN公司为降低标签技术的学习难度，<span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; line-height: 19.5px; color: rgb(255, 0, 0);\"><span style=\"box-sizing: border-box; outline: 0px; font-weight: 700; word-break: break-all;\">在JSP 2.0中定义了一个更为简单、便于编写和调用的SimpleTag接口来实现标签的功能。</span></span></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; line-height: 19.5px; color: rgb(255, 0, 0);\"><span style=\"box-sizing: border-box; outline: 0px; font-weight: 700; word-break: break-all;\">　　<img src=\"https://images0.cnblogs.com/i/289233/201408/162224403114080.png\" alt=\"\"/></span></span></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; line-height: 19.5px; color: rgb(255, 0, 0);\"><span style=\"box-sizing: border-box; outline: 0px; font-weight: 700; word-break: break-all;\">实现SimpleTag接口的标签通常称为简单标签。</span></span>简单标签共定义了5个方法：</p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>setJspContext方法</p></li><li><p>setParent和getParent方法</p></li><li><p>setJspBody方法</p></li><li><p><span style=\"box-sizing: border-box; outline: 0px; word-break: break-all; line-height: 19.5px; color: rgb(255, 0, 0);\"><span style=\"box-sizing: border-box; outline: 0px; font-weight: 700; word-break: break-all;\">doTag方法(非常重要)，简单标签使用这个方法就可以完成所有的业务逻辑</span></span></p></li></ul><h2 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 21px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>二、SimpleTag方法介绍</h2><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2.1、setJspContext方法</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　用于把JSP页面的pageContext对象传递给标签处理器对象</p><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2.2、setParent方法</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　用于把父标签处理器对象传递给当前标签处理器对象</p><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2.3、getParent方法</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">用于获得当前标签的父标签处理器对象</p><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2.4、setJspBody方法</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　用于把代表标签体的JspFragment对象传递给标签处理器对象</p><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>2.5、doTag方法</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　用于完成所有的标签逻辑，包括输出、迭代、修改标签体内容等。在doTag方法中可以抛出javax.servlet.jsp.SkipPageException异常，用于通知WEB容器不再执行JSP页面中位于结束标记后面的内容，这等效于在传统标签的doEndTag方法中返回Tag.SKIP_PAGE常量的情况。</p><h2 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 21px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>三、SimpleTag接口方法的执行顺序</h2><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　当web容器开始执行标签时，会调用如下方法完成标签的初始化：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>WEB容器调用标签处理器对象的setJspContext方法，将代表JSP页面的pageContext对象传递给标签处理器对象。</p></li><li><p>WEB容器调用标签处理器对象的setParent方法，将父标签处理器对象传递给这个标签处理器对象。注意，只有在标签存在父标签的情况下，WEB容器才会调用这个方法。</p></li><li><p>如果调用标签时设置了属性，容器将调用每个属性对应的setter方法把属性值传递给标签处理器对象。如果标签的属性值是EL表达式或脚本表达式，则WEB容器首先计算表达式的值，然后把值传递给标签处理器对象。</p></li><li><p>如果简单标签有标签体，WEB容器将调用setJspBody方法把代表标签体的JspFragment对象传递进来。</p></li><li><p>执行标签时WEB容器调用标签处理器的doTag()方法，开发人员在方法体内通过操作JspFragment对象，就可以实现是否执行、迭代、修改标签体的目的。</p></li></ol><h2 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 21px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>四、开发简单标签实现页面逻辑</h2><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　SUN公司针对SimpleTag接口提供了一个默认的实现类SimpleTagSupport，SimpleTagSupport类中实现了SimpleTag接口的所有方法，因此我们可以编写一个类继承SimpleTagSupport类，然后根据业务需要再重写doTag方法。</p><h3 style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px 0px; font-size: 16px; line-height: 1.5; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><a style=\"box-sizing: border-box; outline: 0px; color: rgb(78, 161, 219); cursor: pointer; word-break: break-all;\"></a>4.1、控制jsp页面某一部分内容是否执行</h3><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　编写一个类继承SimpleTagSupport，然后再重写doTag方法，在doTag方法里面不调用jspFrament.invoke方法即可。</p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">示例代码如下：</p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">SimpleTagDemo1.java</p><p><span class=\"cnblogs_code_copy\" style=\"box-sizing: border-box; outline: 0px; word-break: break-all; padding-right: 5px; line-height: 1.5;\"><a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; border: none;\"><img src=\"https://common.cnblogs.com/images/copycode.gif\" alt=\"复制代码\"/></a></span></p><pre style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 0px; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: &quot;Courier New&quot;; font-size: 14px; line-height: 22px; word-break: break-all; white-space: pre-wrap;\">&nbsp;1&nbsp;package&nbsp;me.gacl.web.simpletag;&nbsp;2&nbsp;&nbsp;3&nbsp;import&nbsp;java.io.IOException;&nbsp;4&nbsp;&nbsp;5&nbsp;import&nbsp;javax.servlet.jsp.JspException;&nbsp;6&nbsp;import&nbsp;javax.servlet.jsp.PageContext;&nbsp;7&nbsp;import&nbsp;javax.servlet.jsp.tagext.JspFragment;&nbsp;8&nbsp;import&nbsp;javax.servlet.jsp.tagext.SimpleTagSupport;&nbsp;9&nbsp;10&nbsp;/**11&nbsp;&nbsp;*&nbsp;@author&nbsp;gacl12&nbsp;&nbsp;*&nbsp;SimpleTagSupport类实现了SimpleTag接口，13&nbsp;&nbsp;*&nbsp;SampleTagDemo1类继承SimpleTagSupport14&nbsp;&nbsp;*/15&nbsp;public&nbsp;class&nbsp;SimpleTagDemo1&nbsp;extends&nbsp;SimpleTagSupport&nbsp;{16&nbsp;17&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*&nbsp;简单标签使用这个方法就可以完成所有的业务逻辑18&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@see&nbsp;javax.servlet.jsp.tagext.SimpleTagSupport#doTag()19&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;重写doTag方法，控制标签体是否执行20&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/21&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@Override22&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;void&nbsp;doTag()&nbsp;throws&nbsp;JspException,&nbsp;IOException&nbsp;{23&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//得到代表jsp标签体的JspFragment24&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;JspFragment&nbsp;jspFragment&nbsp;=&nbsp;this.getJspBody();25&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;26&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//得到jsp页面的的PageContext对象27&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//PageContext&nbsp;pageContext&nbsp;=&nbsp;(PageContext)&nbsp;jspFragment.getJspContext();28&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//调用JspWriter将标签体的内容输出到浏览器29&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//jspFragment.invoke(pageContext.getOut());30&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;31&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//将标签体的内容输出到浏览器32&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jspFragment.invoke(null);33&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;34&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}35&nbsp;}</pre><p><span class=\"cnblogs_code_copy\" style=\"box-sizing: border-box; outline: 0px; word-break: break-all; padding-right: 5px; line-height: 1.5;\"><a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; border: none;\"><img src=\"https://common.cnblogs.com/images/copycode.gif\" alt=\"复制代码\"/></a></span></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　在WEB-INF目录下新建一个simpletag.tld文件，然后在simpletag.tld文件中添加对该标签处理类的描述，如下：</p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　<a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; font-family: &quot;Courier New&quot;; font-size: 12px; background-color: rgb(245, 245, 245); border: none;\"><img src=\"https://img-blog.csdn.net/20180608153906835?watermark/2/text/aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzM2NTAxNTkx/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70\" alt=\"\"/></a></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　simpletag.tld文件代码如下：</p><pre style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 0px; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: &quot;Courier New&quot;; font-size: 14px; line-height: 22px; word-break: break-all; white-space: pre-wrap;\">&nbsp;1&nbsp;&lt;?xml&nbsp;version=&quot;1.0&quot;&nbsp;encoding=&quot;UTF-8&quot;&nbsp;?&gt;&nbsp;2&nbsp;&nbsp;3&nbsp;&lt;taglib&nbsp;xmlns=&quot;http://java.sun.com/xml/ns/j2ee&quot;&nbsp;4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;&nbsp;5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;xsi:schemaLocation=&quot;http://java.sun.com/xml/ns/j2ee&nbsp;http://java.sun.com/xml/ns/j2ee/web-jsptaglibrary_2_0.xsd&quot;&nbsp;6&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;version=&quot;2.0&quot;&gt;&nbsp;7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;description用来添加对taglib(标签库)的描述&nbsp;--&gt;&nbsp;8&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;description&gt;孤傲苍狼开发的SimpleTag自定义标签库&lt;/description&gt;&nbsp;9&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--taglib(标签库)的版本号&nbsp;--&gt;10&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;tlib-version&gt;1.0&lt;/tlib-version&gt;11&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;short-name&gt;GaclSimpleTagLibrary&lt;/short-name&gt;12&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;13&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为自定义标签库设置一个uri，uri以/开头，/后面的内容随便写，如这里的/simpletag&nbsp;，14&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在Jsp页面中引用标签库时，需要通过uri找到标签库15&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在Jsp页面中就要这样引入标签库：&lt;%@taglib&nbsp;uri=&quot;/simpletag&quot;&nbsp;prefix=&quot;gacl&quot;%&gt;16&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;--&gt;17&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;uri&gt;/simpletag&lt;/uri&gt;18&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;19&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--一个taglib(标签库)中包含多个自定义标签，每一个自定义标签使用一个tag标记来描述&nbsp;&nbsp;--&gt;20&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;一个tag标记对应一个自定义标签&nbsp;--&gt;21&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;tag&gt;22&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;description&gt;SimpleTag(简单标签)Demo1&lt;/description&gt;23&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;24&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为标签处理器类配一个标签名，在Jsp页面中使用标签时是通过标签名来找到要调用的标签处理器类的25&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;通过demo1就能找到对应的me.gacl.web.simpletag.SimpleTagDemo1类26&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;--&gt;27&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;name&gt;demo1&lt;/name&gt;28&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;标签对应的处理器类--&gt;29&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;tag-class&gt;me.gacl.web.simpletag.SimpleTagDemo1&lt;/tag-class&gt;30&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--&nbsp;31&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tld文件中有四种标签体类型&nbsp;：empty&nbsp;&nbsp;JSP&nbsp;&nbsp;scriptless&nbsp;&nbsp;tagdepentend&nbsp;&nbsp;\n32&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在简单标签(SampleTag)中标签体body-content的值只允许是empty和scriptless，不允许设置成JSP,如果设置成JSP就会出现异常33&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在传统标签中标签体body-content的值只允许是empty和JSP34&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果标签体body-content的值设置成tagdepentend，那么就表示标签体里面的内容是给标签处理器类使用的，35&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;例如：开发一个查询用户的sql标签，此时标签体重的SQL语句就是给SQL标签的标签处理器来使用的36&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;gacl:sql&gt;SELECT&nbsp;*&nbsp;FROM&nbsp;USER&lt;/gacl:sql&gt;37&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这种情况下，sql标签的&lt;body-content&gt;就要设置成tagdepentend，tagdepentend用得比较少，了解一下即可38&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;--&gt;39&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;body-content&gt;scriptless&lt;/body-content&gt;40&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/tag&gt;41&nbsp;&lt;/taglib&gt;</pre><p><span class=\"cnblogs_code_copy\" style=\"box-sizing: border-box; outline: 0px; word-break: break-all; padding-right: 5px; line-height: 1.5;\"><a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; border: none;\"><img src=\"https://common.cnblogs.com/images/copycode.gif\" alt=\"复制代码\"/></a></span></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　在jsp页面中导入并使用自定义标签，如下：</p><p><span class=\"cnblogs_code_copy\" style=\"box-sizing: border-box; outline: 0px; word-break: break-all; padding-right: 5px; line-height: 1.5;\"><a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; border: none;\"><img src=\"https://common.cnblogs.com/images/copycode.gif\" alt=\"复制代码\"/></a></span></p><pre style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin-top: 0px; margin-bottom: 0px; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: &quot;Courier New&quot;; font-size: 14px; line-height: 22px; word-break: break-all; white-space: pre-wrap;\">&nbsp;1&nbsp;&lt;%@&nbsp;page&nbsp;language=&quot;java&quot;&nbsp;pageEncoding=&quot;UTF-8&quot;%&gt;&nbsp;2&nbsp;&lt;%--在jsp页面中导入自定义标签库&nbsp;--%&gt;&nbsp;3&nbsp;&lt;%@taglib&nbsp;uri=&quot;/simpletag&quot;&nbsp;prefix=&quot;gacl&quot;&nbsp;%&gt;&nbsp;4&nbsp;&lt;!DOCTYPE&nbsp;HTML&gt;&nbsp;5&nbsp;&lt;html&gt;&nbsp;6&nbsp;&nbsp;&nbsp;&lt;head&gt;&nbsp;7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;用简单标签控制标签体是否执行&lt;/title&gt;&nbsp;8&nbsp;&nbsp;&nbsp;&lt;/head&gt;&nbsp;9&nbsp;&nbsp;&nbsp;10&nbsp;&nbsp;&nbsp;&lt;body&gt;11&nbsp;&nbsp;&nbsp;12&nbsp;&nbsp;&nbsp;&lt;%--在jsp页面中使用自定义标签&nbsp;demo1标签是带有标签体的，标签体的内容是&quot;孤傲苍狼&quot;这几个字符串--%&gt;13&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;gacl:demo1&gt;14&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;孤傲苍狼15&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/gacl:demo1&gt;16&nbsp;&nbsp;&nbsp;&lt;/body&gt;17&nbsp;&lt;/html&gt;</pre><p><span class=\"cnblogs_code_copy\" style=\"box-sizing: border-box; outline: 0px; word-break: break-all; padding-right: 5px; line-height: 1.5;\"><a title=\"复制代码\" target=\"_blank\" style=\"box-sizing: border-box; outline: 0px; color: rgb(7, 93, 179); cursor: pointer; word-break: break-all; border: none;\"><img src=\"https://common.cnblogs.com/images/copycode.gif\" alt=\"复制代码\"/></a></span></p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　运行效果如下：</p><p style=\"box-sizing: border-box; outline: 0px; padding: 0px; margin: 10px auto; font-size: 13px; line-height: 26px; text-align: justify; word-break: break-all; white-space: normal; background-color: rgb(255, 255, 255); font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">　　<img src=\"https://images0.cnblogs.com/i/